// ==========================================================
// Main App Component (Главный компонент приложения)
// ==========================================================
const App = () => {
    const [currentPage, setCurrentPage] = useState('products');
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [cartItems, setCartItems] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const debouncedSearchTerm = useDebounce(searchTerm, 300); // Задержка 300ms для debounce

    const navigateTo = (page, product = null) => {
        setCurrentPage(page);
        setSelectedProduct(product);
    };

    const addToCart = (product) => {
        setCartItems(prevItems => {
            const existingItem = prevItems.find(item => item.product.id === product.id);
            if (existingItem) {
                return prevItems.map(item =>
                    item.product.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
                );
            } else {
                return [...prevItems, { product, quantity: 1 }];
            }
        });
        alert(`${product.name} добавлен в корзину!`);
    };

    const removeFromCart = (productId) => {
        setCartItems(prevItems => prevItems.filter(item => item.product.id !== productId));
    };

    const updateQuantity = (productId, newQuantity) => {
        if (newQuantity <= 0) {
            removeFromCart(productId);
            return;
        }
        setCartItems(prevItems =>
            prevItems.map(item =>
                item.product.id === productId ? { ...item, quantity: newQuantity } : item
            )
        );
    };

    const renderPage = () => {
        switch (currentPage) {
            case 'home':
                return <HomePage navigateTo={navigateTo} />;
            case 'products':
                // Передаем отфильтрованные продукты и другие необходимые пропсы
                return <ProductsPage
                            navigateTo={navigateTo}
                            addToCart={addToCart}
                            products={mockProducts}
                            searchTerm={debouncedSearchTerm} // Используем debouncedSearchTerm для фильтрации
                        />;
            case 'productDetail':
                return <ProductDetailPage
                            selectedProduct={selectedProduct}
                            navigateTo={navigateTo}
                            addToCart={addToCart}
                        />;
            case 'cart':
                return <CartPage
                            cartItems={cartItems}
                            navigateTo={navigateTo}
                            removeFromCart={removeFromCart}
                            updateQuantity={updateQuantity}
                        />;
            default:
                return <HomePage navigateTo={navigateTo} />;
        }
    };

    return (
        <div className="min-h-screen flex flex-col font-sans bg-gray-50">
            <Header
                currentPage={currentPage}
                navigateTo={navigateTo}
                cartItems={cartItems}
                searchTerm={searchTerm} // Для input.value используем НЕзадебаунсенное значение
                setSearchTerm={setSearchTerm}
            />
            <main className="flex-grow p-4">
                {renderPage()}
            </main>
            <Footer />
        </div>
    );
};

export default App;
