import React, { useState, useEffect } from 'react';

// Главный компонент приложения
const App = () => {
    // Состояние для отслеживания текущей страницы
    const [currentPage, setCurrentPage] = useState('products');

    // Функция для навигации по страницам
    const navigateTo = (page) => {
        setCurrentPage(page);
    };

    // Компонент заголовка и навигации
    const Header = () => (
        <header className="bg-gray-800 text-white p-4 shadow-md rounded-b-lg">
            <div className="container mx-auto flex flex-col sm:flex-row justify-between items-center">
                <h1 className="text-3xl font-bold text-indigo-300 mb-2 sm:mb-0">
                    ПРИДУМАЙ НОРМАЛЬНОЕ НАЗВАНИЕ МАГАЗИНА
                </h1>
                <nav>
                    <ul className="flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-6 text-lg">
                        <li>
                            <button
                                onClick={() => navigateTo('products')}
                                className={`px-3 py-2 rounded-lg transition-colors duration-200
                                    ${currentPage === 'products' ? 'bg-indigo-700 text-white' : 'hover:bg-gray-700'}
                                    focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-opacity-50`}
                            >
                                Товары
                            </button>
                        </li>
                        <li>
                            <button
                                onClick={() => navigateTo('cart')}
                                className={`px-3 py-2 rounded-lg transition-colors duration-200
                                    ${currentPage === 'cart' ? 'bg-indigo-700 text-white' : 'hover:bg-gray-700'}
                                    focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-opacity-50`}
                            >
                                Корзина
                            </button>
                        </li>

                        <li>
                            <button
                                onClick={() => navigateTo('home')}
                                className={`px-3 py-2 rounded-lg transition-colors duration-200
                                    ${currentPage === 'home' ? 'bg-indigo-700 text-white' : 'hover:bg-gray-700'}
                                    focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-opacity-50`}
                            >
                                Профиль
                            </button>
                        </li>
                    </ul>
                </nav>
            </div>
        </header>
    );

    // Компонент главной страницы
    const HomePage = () => (
        <section className="py-12 bg-gradient-to-r from-purple-500 to-indigo-600 text-white rounded-lg shadow-xl text-center m-4">
            <div className="container mx-auto px-4">
                <h2 className="text-5xl font-extrabold mb-4 animate-fadeInDown">
                    Добро пожаловать в наш Merch Shop!
                </h2>
                <p className="text-xl leading-relaxed mb-8 animate-fadeIn">
                    Найдите уникальный мерч от ваших любимых брендов, игр и артистов.
                </p>
                <button
                    onClick={() => navigateTo('products')}
                    className="bg-white text-indigo-700 font-bold py-3 px-8 rounded-full shadow-lg hover:bg-gray-100 transform hover:scale-105 transition duration-300 ease-in-out focus:outline-none focus:ring-4 focus:ring-white focus:ring-opacity-70"
                >
                    Начать покупки
                </button>
            </div>
        </section>
    );

    // Мок-данные для товаров
    const mockProducts = [
        {
            id: 1,
            name: 'Футболка "Космический Путешественник"',
            price: 19.99,
            image: 'https://placehold.co/400x300/6b7280/ffffff?text=Футболка', // Placeholder изображение
            description: 'Высококачественная хлопковая футболка с уникальным принтом.',
        },
        {
            id: 2,
            name: 'Кружка "Программист"',
            price: 12.50,
            image: 'https://placehold.co/400x300/4b5563/ffffff?text=Кружка',
            description: 'Кружка для настоящих мастеров кода.',
        },
        {
            id: 3,
            name: 'Худи "Cyberpunk City"',
            price: 49.99,
            image: 'https://placehold.co/400x300/374151/ffffff?text=Худи',
            description: 'Уютное худи для фанатов киберпанка.',
        },
        {
            id: 4,
            name: 'Брелок "Маленький Дракон"',
            price: 7.99,
            image: 'https://placehold.co/400x300/1f2937/ffffff?text=Брелок',
            description: 'Милый брелок для ключей или рюкзака.',
        },
    ];

    // Компонент страницы товаров
    const ProductsPage = () => {
        return (
            <section className="py-8 px-4 bg-gray-100 min-h-screen">
                <div className="container mx-auto">
                    <h2 className="text-4xl font-bold text-gray-800 mb-8 text-center">Наши Товары</h2>
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
                        {mockProducts.map((product) => (
                            <div key={product.id} className="bg-white rounded-xl shadow-lg overflow-hidden transform hover:scale-105 transition duration-300 ease-in-out flex flex-col">
                                <img
                                    src={product.image}
                                    alt={product.name}
                                    className="w-full h-48 object-cover"
                                    onError={(e) => { e.target.onerror = null; e.target.src = 'https://placehold.co/400x300/6b7280/ffffff?text=Image+Not+Found'; }}
                                />
                                <div className="p-6 flex flex-col flex-grow">
                                    <h3 className="text-xl font-semibold text-gray-900 mb-2">{product.name}</h3>
                                    <p className="text-gray-600 text-sm mb-4 flex-grow">{product.description}</p>
                                    <div className="flex justify-between items-center mt-auto">
                                        <span className="text-2xl font-bold text-indigo-600">${product.price.toFixed(2)}</span>
                                        <button className="bg-indigo-600 text-white px-5 py-2 rounded-full hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-opacity-50 transition-colors duration-200">
                                            Добавить в корзину
                                        </button>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </section>
        );
    };

    // Компонент страницы корзины
    const CartPage = () => (
        <section className="py-8 px-4 bg-gray-100 min-h-screen">
            <div className="container mx-auto">
                <h2 className="text-4xl font-bold text-gray-800 mb-8 text-center">Ваша Корзина</h2>
                <div className="bg-white rounded-xl shadow-lg p-6 text-center">
                    <p className="text-gray-600 text-lg">Ваша корзина пока пуста.</p>
                    <button
                        onClick={() => navigateTo('products')}
                        className="mt-6 bg-indigo-600 text-white font-bold py-2 px-6 rounded-full hover:bg-indigo-700 transition duration-300 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-opacity-50"
                    >
                        Вернуться к покупкам
                    </button>
                </div>
            </div>
        </section>
    );

    // Компонент подвала
    const Footer = () => (
        <footer className="bg-gray-800 text-white p-4 mt-8 shadow-inner rounded-t-lg">
            <div className="container mx-auto text-center text-sm">
                &copy; {new Date().getFullYear()} Merch Shop. Все права защищены.
            </div>
        </footer>
    );

    // Рендеринг содержимого страницы в зависимости от currentPage
    const renderPage = () => {
        switch (currentPage) {
            case 'home':
                return <HomePage />;
            case 'products':
                return <ProductsPage />;
            case 'cart':
                return <CartPage />;
            default:
                return <HomePage />;
        }
    };

    return (
        <div className="min-h-screen flex flex-col font-sans bg-gray-50">
            {/* Tailwind CSS CDN, загружается один раз для всего приложения */}
            <Header />
            <main className="flex-grow p-4">
                {renderPage()}
            </main>
            <Footer />
        </div>
    );
};

export default App;
